    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        float totale = 0;
        if(jCheckBox1.isSelected()) {
            totale += Float.parseFloat(jTextField8.getText()) * Integer.parseInt(jTextField1.getText());
        }
        if(jCheckBox2.isSelected()) {
            totale += Float.parseFloat(jTextField7.getText()) * Integer.parseInt(jTextField2.getText());
        }
        if(jCheckBox3.isSelected()) {
            totale += Float.parseFloat(jTextField6.getText()) * Integer.parseInt(jTextField3.getText());
        }
        if(jCheckBox4.isSelected()) {
            totale += Float.parseFloat(jTextField5.getText()) * Integer.parseInt(jTextField4.getText());
        }
        jLabel8.setText(String.valueOf(totale));
    }//GEN-LAST:event_jButton1ActionPerformed

